mapper.select:
SELECT g.goods_no goodsNo , g.goods_title goodsTitle , g.goods_memo goodsMemo , g.goods_price goodsPrice , g.goods_state goodsState , g.update_date updateDate , g.create_date createDate , f.goods_file_name goodsFileName , f.goods_file_ext goodsFileExt , c.category_no categoryNo FROM goods g LEFT OUTER JOIN goods_file f ON g.goods_no = f.goods_no LEFT OUTER JOIN goods_category c ON g.goods_no = c.goods_no
goods_title LIKE CONCAT('%', #{searchWord}, '%')
c.category_no = #{categoryNo}
ORDER BY goodsNo DESC LIMIT #{beginRow}, #{rowPerPage};

SELECT g.goods_no goodsNo , g.goods_title goodsTitle , g.goods_memo goodsMemo , g.goods_price goodsPrice , g.goods_state goodsState , g.update_date updateDate , g.create_date createDate , f.goods_file_name goodsFileName , f.goods_file_ext goodsFileExt FROM goods g LEFT OUTER JOIN goods_file f ON g.goods_no = f.goods_no
goods_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY goodsNo DESC LIMIT #{beginRow}, #{rowPerPage};

mapper.update:
UPDATE goods SET 
	goods_title = #{goodsTitle}
	, goods_memo = #{goodsMemo}
	, goods_price = #{goodsPrice}
	, goods_state = #{goodsState}
	, update_date = NOW()
WHERE goods_no = #{goodsNo}

mapper.insert:

INSERT INTO goods (
	goods_title
	, goods_memo
	, goods_price
	, goods_state)
VALUES (
	#{goodsTitle}
	, #{goodsMemo}
	, #{goodsPrice}
	, #{goodsState})
